&lt;&#x2F;span&gt;&lt;span style=&quot;background-color:#002b36;color:#859900;&quot;&gt;!&lt;&#x2F;span&gt;&lt;span style=&quot;background-color:#002b36;color:#839496;&quot;&gt; Dark
&lt;&#x2F;span&gt;&lt;span style=&quot;background-color:#002b36;color:#586e75;&quot;&gt;#&lt;&#x2F;span&gt;&lt;span style=&quot;background-color:#002b36;color:#586e75;&quot;&gt;define S_base03        #002b36
&lt;&#x2F;span&gt;&lt;span style=&quot;background-color:#002b36;color:#586e75;&quot;&gt;#&lt;&#x2F;span&gt;&lt;span style=&quot;background-color:#002b36;color:#586e75;&quot;&gt;define S_base02        #073642
&lt;&#x2F;span&gt;&lt;span style=&quot;background-color:#002b36;color:#586e75;&quot;&gt;#&lt;&#x2F;span&gt;&lt;span style=&quot;background-color:#002b36;color:#586e75;&quot;&gt;define S_base01        #586e75
&lt;&#x2F;span&gt;&lt;span style=&quot;background-color:#002b36;color:#586e75;&quot;&gt;#&lt;&#x2F;span&gt;&lt;span style=&quot;background-color:#002b36;color:#586e75;&quot;&gt;define S_base00        #657b83
&lt;&#x2F;span&gt;&lt;span style=&quot;background-color:#002b36;color:#586e75;&quot;&gt;#&lt;&#x2F;span&gt;&lt;span style=&quot;background-color:#002b36;color:#586e75;&quot;&gt;define S_base0         #839496
