&lt;&#x2F;span&gt;&lt;span style=&quot;background-color:#002b36;color:#839496;&quot;&gt;    &lt;&#x2F;span&gt;&lt;span style=&quot;background-color:#002b36;color:#839496;&quot;&gt;\ ? substitute(g:colors_name, &amp;#39;dark&amp;#39;, &amp;#39;light&amp;#39;, &amp;#39;&amp;#39;)
