&lt;&#x2F;span&gt;&lt;span style=&quot;background-color:#002b36;color:#839496;&quot;&gt;  &lt;&#x2F;span&gt;&lt;span style=&quot;background-color:#002b36;color:#839496;&quot;&gt;exe &amp;#39;colors&amp;#39; l:schemes[((a:delta+index(l:schemes, g:colors_name)) % 4 + 4) % 4]
